string s;
bool isgood(int l, int r) {
    for(int l2 = l; l2 <= r; l2 ++) {
        for(int i = 1; i <= 5 && l2 + 2 * i <= r; i++) {
            if(s[l2] == s[l2 + i] && s[l2 + i] == s[l2 + (2 * i)]) {
                return false;
            }
        }
    }
