const int inf = 1e9;
struct Node {
    Node *l = 0, *r = 0;
    int lo, hi, mset = inf, madd = 0, val = -inf, val2 = inf;
    Node(int lo,int hi):lo(lo),hi(hi){} // Large interval of -inf
    Node(vi& v, int lo, int hi) : lo(lo), hi(hi) {
        if (lo + 1 < hi) {
            int mid = lo + (hi - lo)/2;
            l = new Node(v, lo, mid); r = new Node(v, mid, hi);
            val = max(l->val, r->val);
            val2 = min(l->val2, r->val2);
        }
        else val = val2 = v[lo];
    }
    int query(int L, int R) {
        if (R <= lo || hi <= L) return -inf;
        if (L <= lo && hi <= R) return val;
        push();
        return max(l->query(L, R), r->query(L, R));
    }
    int query2(int L, int R) {
        if (R <= lo || hi <= L) return inf;
        if (L <= lo && hi <= R) return val2;
        push();
        return min(l->query2(L, R), r->query2(L, R));
    }
    void add(int L, int R, int x) {
        if (R <= lo || hi <= L) return;
        if (L <= lo && hi <= R) {
            madd += x;
            val += x;
            val2 += x;
        }
        else {
            push(), l->add(L, R, x), r->add(L, R, x);
            val = max(l->val, r->val);
            val2 = min(l->val2, r->val2);
        }
    }
    void set(int L, int R, int x) { 
        if (R <= lo || hi <= L) return;
        if (L <= lo && hi <= R) mset = val = val2 = x, madd = 0;
        else {
            push(), l->set(L, R, x), r->set(L, R, x);
            val = max(l->val, r->val);
            val2 = min(l->val2, r->val2);
        }
    }
    void push() {
        if (!l) {
            int mid = lo + (hi - lo)/2;
            l = new Node(lo, mid); r = new Node(mid, hi);
        }
        if (mset != inf)
            l->set(lo,hi,mset), r->set(lo,hi,mset), mset = inf;
        else if (madd)
            l->add(lo,hi,madd), r->add(lo,hi,madd), madd = 0;
    }
};
vector<int> vec(2e5 + 501, inf);
Node* tr = new Node(vec, 0, vec.size());
void solve() {
    int n;
    cin >> n;
    vi a(n + 1), b(n + 1);
    vector<pii> order;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        order.pb({a[i], i});
    }
    sort(all(order));
    for(int i = 1; i <= n; i++) {
        cin >> b[i];
    }
    tr -> set(0, (int) 2e5 + 500, n + 1);
