    vector<int> a_right(n + 1);
    a_right[n] = n + 1;
    tr -> set(a[n], a[n] + 1, n);
    for(int i = n - 1; i >= 1; i--) {
        int q = min(n + 1, tr -> query2(a[i] + 1, 2e5 + 400));
        a_right[i] = q;
        tr -> set(a[i], a[i] + 1, i);
    }
    tr -> set(0, 2e5 + 500, 0);

