    vector<int> a_left(n + 1);
    a_left[1] = 0;
    tr -> set(a[1], a[1] + 1, 1);
    for(int i = 2; i <= n; i++) {
        int q = max(0ll, tr -> query(a[i] + 1, 2e5 + 400));
        a_left[i] = q;
        tr -> set(a[i], a[i] + 1, i);
    }
    tr -> set(0, 2e5 + 500, n + 1);
    // find smallest j s.t j > i and b[j] < a[i]
    vector<int> b_right(n + 1);
    b_right[n] = n + 1;
    tr -> set(b[n], b[n] + 1, n);
