        int q = min(n + 1, tr -> query2(0, a[i]));
        b_right[i] = q;
        tr -> set(b[i], b[i] + 1, i);
    }
    tr -> set(0, 2e5 + 500, 0);
    // find largest j s.t j < i and b[j] < a[i];
    vector<int> b_left(n + 1);
    b_left[1] = 0;
    tr -> set(b[1], b[1] + 1, 1);
    for(int i = 2; i <= n; i++) {
        int q = max(0ll, tr -> query(0, a[i]));
        b_left[i] = q;
        tr -> set(b[i], b[i] + 1, i);
    }
    tr -> set(0, 2e5 + 500, 0);
    for(int i = 1; i <= n; i++) {tr -> set(i, i + 1, a[i]);}
