            if(a[i] > a[idx] || a[idx] > b[i]) break;
            else a[i] = a[idx];
        }
    }
    for(int i = 0; i < n; i++) {
        if(a[i] != b[i]) {
            cout << "NO" << endl;
            return;
        }
    }
    cout << "YES" << endl;
    return;
}
