    return mmul(a, fastpow(b, mod - 2));
}
int find(int x){
    return dsu[x] == x ? x : dsu[x] = find(dsu[x]);
}
void modify(int x,int y){
    int pa = find(x);
    int pb = find(y);
    if(pa != pb) {
        // if(rk[pa] < rk[pb]) swap(pa, pb); // pa > pb
        dsu[pb] = pa;
        rk[pa] += rk[pb];
    }
}
void init(int n) {
    for(int i = 1; i <= 2 * n; i++) {
        dsu[i] = i;
        if(i <= n) rk[i] = 1;
        else rk[i] = 0;
    }
}
void dfs(int node, int prev, int val) {
    if(node <= n) {
        ans[node] = val;
        return;
    }
    for(auto [c, w] : graph[node]) {
        if(c == prev) continue;
        dfs(c, node, madd(val, w));
    }
}
void solve() {
    cin >> n;
    init(n);
    int idx = n + 1;
    for(int i = 0; i < n - 1; i++) {
        int a, b;
        cin >> a >> b;
        int node_a = find(a);
        int node_b = find(b);
        graph[idx].pb({node_a, mdiv(rk[node_a], rk[node_a] + rk[node_b])});
        graph[idx].pb({node_b, mdiv(rk[node_b], rk[node_a] + rk[node_b])});
        modify(node_a, node_b);
        modify(idx, node_a);
        idx += 1;
    }
    dfs(idx - 1, -1, 0);
    for(int i = 1; i <= n; i++) {
        cout << ans[i] << " ";
    }
    cout << endl;
    return;
}
```

Time Complexity: $O(nlog(\text{mod}))$ (due to mod division which is equal to $log(\text{mod})$)

