void solve() {
    int n, b;
    cin >> n >> b;
    vector<int> p;
    vector<int> times;
    for(int i = 2; i * i <= b; i++) {
        // factorize b;
        if(b == 1) break;
        while(b % i == 0) {
            if(!p.size() or (p.size() and p[p.size() - 1] != i)) {
                p.pb(i);
                times.pb(0);
            }
            b /= i;
            times[times.size() - 1] += 1;
        }
    }
    if(b != 1) {
        p.pb(b);
        times.pb(1);
    }
    int ans = 5000000000000000000ll;
    for(int i = 0; i < p.size(); i++) {
        int cnt = 0;
        int p_i = 1;
        for(int j = 1; p_i <= n / p[i]; j++) {
            p_i *= p[i];
            cnt += (n / p_i);
        }
        ans = min(ans, (cnt / times[i]));
    }
    cout << ans << endl;
    return;
}
```

