    for(int i = n; i >= 1; i--) {
        if(s.find(vec[i]) == s.end()) rightcan[i] += 1;
        rightcan[i] += rightcan[i + 1];
        s.insert(vec[i]);
    }
    s.clear();
    int ans = 0;
    for(int i = 1; i <= n; i++) {
        if(s.find(vec[i]) == s.end()) ans += rightcan[i];
        s.insert(vec[i]);
    }
    cout << ans << endl;
    return;
}
```

